package jspexp.z01_vo;

public class Emp3 {
	private int deptno; 
	private double msal;
	private double asal;
	public Emp3() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Emp3(int deptno, double msal, double asal) {
		super();
		this.deptno = deptno;
		this.msal = msal;
		this.asal = asal;
	}
	public int getDeptno() {
		return deptno;
	}
	public void setDeptno(int deptno) {
		this.deptno = deptno;
	}
	public double getMsal() {
		return msal;
	}
	public void setMsal(double msal) {
		this.msal = msal;
	}
	public double getAsal() {
		return asal;
	}
	public void setAsal(double asal) {
		this.asal = asal;
	}
	
	
}
